/* eslint-disable no-unused-vars */
/* eslint-disable react/prop-types */
import React from "react";
import classes from "./Content3.module.css";


let personData = [
    {input: "Имя",
    type: "text",
    value: "Елена"},
    {input: "Мобильный телефон",
    type: "phone",
    value: "+38 (067) 3254989"},
    {input: "Электронная почта",
    type: "e-mail",
    value: "o.sonina1986@gmail.com"},
    {input: "Фактический город проживания",
    type: "text",
    value: "Хмельницкий"},
    {input: "Место работы",
    type: "textarea",
    value: "ФЛП Басистый М.И."},
    {input: "Должность",
    type: "textarea",
    value: "аналитик консолидированной информации"}
];

class Trcomponent extends React.Component {
    render() {
      return (
          <div>
            <p>{this.props.tdI}</p>
            <input type={this.props.tdT} value={this.props.tdV}></input>
          </div>
      );
    }
  }

class Tbody extends React.Component {
    render() {
        const i = [];
        for (const [index, value] of personData.entries()) {
        i.push(<Trcomponent className={classes.item} tdI = {personData[index].input} tdT = {personData[index].type} tdV = {<input>{personData[index].value}</input>} />);
    }
        return (
            <div>
               {i}
            </div>
        );
    }
}

class Content3 extends React.Component {
    render() {
        return (
            <div>
                <h1>Персональная страница</h1>
                <form className={classes.table}>
                    <Tbody />
                </form>
            </div>
        );
    }
}

export default Content3;
