/* eslint-disable no-unused-vars */
/* eslint-disable react/prop-types */
import React from "react";
import classes from "./Content3.module.css";


let personData = [
    {input: "Имя:  ",
    type: "text",
    value: "Елена"},
    {input: "Мобильный телефон:  ",
    type: "phone",
    value: "+38 (067) 3254989"},
    {input: "Электронная почта:  ",
    type: "e-mail",
    value: "o.sonina1986@gmail.com"},
    {input: "Фактический город проживания:  ",
    type: "text",
    value: "Хмельницкий"},
    {input: "Место работы:  ",
    type: "text",
    value: "ФЛП Басистый М.И."},
    {input: "Должность:  ",
    type: "text",
    value: "аналитик консолидированной информации"}
];

class Trcomponent extends React.Component {
    render() {
      return (
          <div className={classes.item}>
          <label>
            {this.props.tdI}
            <input type={this.props.tdT} placeholder={this.props.tdV} value={this.props.value} onChange={this.handleChange} /><br />
          </label>
          </div>
      );
    }
  }

class Tbody extends React.Component {
    render() {
        const { facts } = this.props;
        const i = [];
        for (const [index, value] of personData.entries()) {
        i.push(<Trcomponent className={classes.item} tdI = {personData[index].input} tdT = {personData[index].type} tdV = {personData[index].value} value = {facts} />);
    }
        return (
            <div>
               {i}
            </div>
        );
    }
}

class Content3 extends React.Component {
    constructor(props) {
      super(props);
      this.state = {
        facts: personData.value
      };

        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
      }

      handleChange(event) {
        this.setState({value: event.target.value});
        console.log(event.target.value, this);
      }

      handleSubmit(event) {
        this.setState({value: "1"});
        event.preventDefault();
      }
    render() {
        return (
            <div>
                <h1>Персональная страница</h1>
                <form className={classes.table}>
                    <Tbody facts={this.state.facts} />
                    console.log(this.state.facts);
                    <button type="submit" onClick={ () => { alert("Hi") }}>Возврат начальных значений</button>
                </form>
            </div>
        );
    }
}

export default Content3;
